import React from 'react'
import Row from 'react-bootstrap/Row'
import  Col from 'react-bootstrap/Col'
import  Button from 'react-bootstrap/Button'

export default function Form({ form, fields, button }) {

    const fil = fields.filter((i)=>i.group == "additional")
    console.log(fil)

    return (
        <div>
            <form>
                <h4>{form.title}</h4>
                <Row>
                {fields.map((i) => <Col>{i.label} <input name={i.name} type={i.type} group={i.group} required={i.required} /></Col>)}
                </Row>
            </form>
            <Button>{button}</Button>
        </div>
    )
}
