import React from 'react'
import Card from './Card'
import Slider from "react-slick"
import './List.css'


export default function List({ items, titleImg }) {
    // console.log(typeof(items));
    // console.log(items);
    const slidesShow = 3
    const resize = () => {
        if (window.screen.width > 800) {
            return slidesShow = 1
        } else {
            return slidesShow = 3
        }
    }


    return (

        <div>

            <h3> {titleImg}</h3>
            <Slider
                arrows={true}
                speed={500}
                autoplay={true}
                autoplaySpeed={3000}
                slidesToShow={resize}
                slidesToScroll={3}
                dots={true}
                // centerMode={true}
                swipeToSlide={true}
                className={'slide'}

            >
                {items.map((item, index) => {
                    return <Card img={item} key={index} />

                })}
            </Slider>
                {console.log(resize())}
        </div >


    )
}
