import React from 'react'
import Card from './Card'
import Slider from "react-slick"
import './List.css'


export default function List({ items, titleImg }) {
    // console.log(typeof(items));
    // console.log(items);


    return (

        <div>
            {/* {screen.width > 800 ? alert(123): alert(321) } */}
                <h3> { titleImg }</h3>
       {window.screen.width < '800px' ? <Slider
            arrows={true}
            speed={500}
            autoplay={true}
            autoplaySpeed={3000}
            slidesToShow={3}
            slidesToScroll={3}
            dots={true}
            // centerMode={true}
            swipeToSlide={true}
            className={'slide'}

        >
            {items.map((item, index) => {
                return <Card img={item} key={index} />

            })}
        </Slider> : <h1>JJJJJ</h1>}

            </div >


    )
}
