import React from 'react'
import Row from 'react-bootstrap/Row'
import  Col from 'react-bootstrap/Col'
import  Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'

export default function Forms({ form, fields, button }) {

    const main = fields.filter((i)=> i.group === 'main')
    const additional = fields.filter((i)=>i.group === "additional")


    return (
        <div>
                <h4>{form.title}</h4>
            <Form.Row>
                <Form.Group md={8}>
                {main.map((i) => <Form.label>{i.label}</Form.label>)}
                </Form.Group>
                <Col md={4}>
                    {additional.map((i)=> <label>{i.label} <input name={i.name} type={i.type} group={i.group} required={i.required} /></label>)}
                </Col>
            <Button>{button}</Button>
            </Form.Row>
        </div>
    )
}
