import React from 'react'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'

export default function Forms({ form, fields, button }) {

    const main = fields.filter((i) => i.group === 'main')
    const additional = fields.filter((i) => i.group === "additional")


    return (
        <div>
            <h4>{form.title}</h4>
            <Form.Row md={12}>
                <Row>
                    {main.map((i) => <Form.Group> <Form.Label >{i.label} <Form.Control name={i.name} type={i.type} group={i.group} required={i.required} /></Form.Label> </Form.Group>)}
                </Row>
                <Row>
                    {additional.map((i) => <Form.Group> <Form.Label>{i.label} <Form.Control as='textarea' name={i.name} type={i.type} group={i.group} required={i.required} /></Form.Label> </Form.Group>)}
                </Row>
            </Form.Row>
            <Button>{button}</Button>
        </div>
    )
}
