import React from 'react'
import Card from './Card'
import Slider from "react-slick"
import './List.css'


export default function List({ items, titleImg }) {
    // console.log(typeof(items));
    // console.log(items);
    const resize = () => {
        let slidesShow = 3
        if (window.screen.width > 800) {

            return slidesShow = 3
        } else {

            return slidesShow = 1
        }
    }


    return (
        <div>

            <div className="bigSlider">

                <h3 className="titleImg"> {titleImg}</h3>
                <Slider
                    arrows={true}
                    speed={500}
                    autoplay={true}
                    autoplaySpeed={3000}
                    slidesToShow={3}
                    slidesToScroll={3}
                    dots={true}
                    // centerMode={true}
                    swipeToSlide={true}
                    className={'slide'}
                // variableWidth={true}

                >
                    {items.map((item, index) => {
                        return <Card img={item} key={index} />

                    })}
                </Slider>


            </div >
            <div className="smallSlider">

                <h3> {titleImg}</h3>
                <Slider
                    arrows={true}
                    speed={500}
                    autoplay={true}
                    autoplaySpeed={3000}
                    slidesToShow={1}
                    slidesToScroll={1}
                    dots={true}
                    // centerMode={true}
                    swipeToSlide={true}
                    className={'slide'}

                >
                    {items.map((item, index) => {
                        return <Card img={item} key={index} />

                    })}
                </Slider>

            </div >


        </div>
    )
}
