import React from 'react'
// import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import './Form.css'

export default function Forms({ form, fields, button }) {

    const main = fields.filter((i) => i.group === 'main')
    const additional = fields.filter((i) => i.group === "additional")


    return (
        <div>
            <h4>{form.title}</h4>
            <Form.Row as={Col} md={12}>

                {main.map((i) => <Form.Group as={Col} md={5} xs={6}>
                    <Form.Label as={Col} md={12}>{i.label}
                        <Form.Control name={i.name} type={i.type} group={i.group} required={i.required} />
                    </Form.Label>
                </Form.Group>)}
                {additional.map((i) => <Form.Group className="formComments" as={Col} md={2}>
                    <Form.Label  className="formTextarea" as={Col} md={12}>{i.label}
                        <Form.Control as='textarea' name={i.name} type={i.type} group={i.group} required={i.required} />
                    </Form.Label>
                </Form.Group>)}
            </Form.Row>
            <Button>{button}</Button>
        </div>
    )
}
